import React, { Component } from 'react';
import axios from 'axios';
import { MDBRow, MDBCol, MDBCard, MDBCardBody, MDBCardTitle, MDBCardText, MDBCardFooter } from "mdbreact";

class Search extends Component {

  state = {
    search : ""
  }

  componentDidMount() {    
    axios.get('http://localhost:8080/book.json').then(res => {
        this.setState({ book: res.data })
        console.log("state" , res.data)
    })
    .catch(function (error) {
      console.log(error);
  })
}

renderBooks = list =>{

  const {search} = this.state;
  var name = list.name.toLowerCase()

  if( search !== "" && name.indexOf(search)=== -1){
    return null
  }
  return(
    <MDBRow>        
        <MDBCol lg="3" md="4" className="mb-lg-0">
        <MDBCard wide ecommerce>
            <MDBCardBody cascade  placement="top"
                    tag="a"
                    component="i"
                    componentClass="fa fa-eye grey-text ml-3"
                    tooltipContent="Quick look"
                    className="text-center">
              <MDBCardTitle>
                <strong id="color">
                    <h2 id="tex">{list.titre}</h2>
                </strong>
                    <h3>Name: { list.name }</h3>
                    <p>Author name: {list.author.name}</p>
                    <p>({ list.author.gender })</p>
                    <p>Genre: { list.genre }</p>
                    <p>Publish​ ​date: {list.date}</p>
              </MDBCardTitle>
              <MDBCardText>
                <h6>{list.description}</h6>
              </MDBCardText>
              <MDBCardFooter className="px-1">
                <span className="float-left font-weight-bold">
                </span>
                <span className="float-right">
                </span>
              <div className="">

              </div>
              </MDBCardFooter>

            </MDBCardBody>
          </MDBCard>

      </MDBCol>
    </MDBRow>
);
}

onchange = e => {
  this.setState({search: e.target.value});
}

  render() {
    return (

      //console.log(films)
      <div className="container">         
        <div className="row">
          <div className="col-md-1"></div>
          <div className="col-md-10">
            <input className="searchin" placeholder="Titre book..." onChange={this.onchange} />
              {
                  axios.get('http://localhost:8080/book.json').then(res => {
                    this.setState({ book: res.data })
                    console.log("state" , res.data)
                    res.data.map(list=>{
                        this.renderBooks(list)
                    })

                })
                .catch(function (error) {
                  console.log(error);
              })


                }
            }

          </div>
          <div className="col-md-1"></div>
        </div>   
      </div>


    );
  }
}

export default Search;
