import React, { Component } from 'react';
import axios from 'axios';
import { MDBRow, MDBCol, MDBCard, MDBCardBody, MDBCardTitle, MDBCardText, MDBCardFooter } from "mdbreact";
// import Modal from './modal';
export default class Acceuil extends Component {
  constructor(props) {
    super(props);

    this.state = {
        book: [],
        name:"",
        author: {
          name:"",
          gender:""
        },
        genre:"",
        date:""
    };
    this.handleChange = this.handleChange.bind(this);

}
componentDidMount() {    
    axios.get('http://localhost:8080/book.json').then(res => {
        console.log(res.data)
        this.setState({book:res.data})
        console.log(this.state.book);

    }).catch(function (error) {
      console.log(error);
  })
}

handleChange(e){
    this.setState({
        [e.target.name]: e.target.value
    })
}
render() {


    return (
    <MDBRow>
    {this.state.book.length>0 ?(this.state.book.map( list=>{
      console.log(list);

      return(
   <MDBCol lg="3" md="4" className="mb-lg-0">
        <MDBCard wide ecommerce>
            <MDBCardBody cascade  placement="top"
                    tag="a"
                    component="i"
                    componentClass="fa fa-eye grey-text ml-3"
                    tooltipContent="Quick look"
                    className="text-center">

              <MDBCardText>
              <h6 >Name: {list.name}</h6>
              <h6 >Author: {list.author.name}({list.author.gender})</h6>
              <h6 >Genre: { list.genre } heures</h6>
              <h6 >Publish​ ​ date: { list.date } heures</h6>
              </MDBCardText>
              <MDBCardFooter className="px-1">
                <span className="float-left font-weight-bold">
                </span>
                <span className="float-right">
                </span>
              <div className="">
              </div>
              </MDBCardFooter>

            </MDBCardBody>
          </MDBCard>

      </MDBCol>
      )})):""} 
    </MDBRow>


        )
    }
}
